import React, { useEffect, useState } from 'react';

const APoD = () => {
	const [date, setDate] = useState('');

	useEffect(() => {
		formatToday();
	}, []);

	const formatToday = () => {
		const today = new Date();

		const options = {
			weekday: 'short',
			year: 'numeric',
			month: '2-digit',
			day: 'numeric',
		};

		console.log(today.toLocaleString('en', options));
	};

	const handleDateChange = (e) => {
		setDate(e.target.value);
	};

	return (
		<div className="APoD">
			<input type="date" value={date} onChange={handleDateChange} />
		</div>
	);
};

export default APoD;
