import React, { useEffect, useState } from 'react';
import apiKey from '../secrets';

import APoDResult from './APoDResult';

const APoD = () => {
	const [date, setDate] = useState('');
	const [isLoading, setIsLoading] = useState(false);
	const [hasError, setHasError] = useState(false);
	const [hasResult, setHasResult] = useState(false);
	const [result, setResult] = useState({});

	useEffect(() => {
		setDate(formatToday());
	}, []);

	const formatToday = () => {
		const today = new Date();

		const options = {
			month: '2-digit',
		};

		return `${today.getFullYear()}-${today.toLocaleString('en', options)}-${today.getDate()}`;
	};

	const handleDateChange = (e) => {
		setDate(e.target.value);
	};

	const getAPoD = async () => {
		setIsLoading(true);

		const options = {
			mode: 'cors',
		};

		try {
			await fetch(`https://api.nasa.gov/planetary/apod?date=${dates}&api_key=${apiKey}`, options).then(async function (
				response
			) {
				if (response.ok) {
					const data = await response.json();
					setResult({ ...data });
					setHasResult(true);
				} else {
					setHasError(true);
				}
			});
		} catch (err) {
			setHasError(true);
		} finally {
			setIsLoading(false);
		}
	};

	return (
		<div className="APoD">
			<>
				<h1>Pick a date</h1>
				<input type="date" value={date} onChange={handleDateChange} />
				<button onClick={getAPoD}>Get APoD</button>
			</>

			{isLoading && <div>Searching the cosmos</div>}

			{hasError && <div>An error has occurred</div>}

			{hasResult && <APoDResult />}
		</div>
	);
};

export default APoD;
