import React from 'react';

const APoDResult = ({ date, explanation, media_type, title, url }) => {
	return (
		<div>
			<h2>
				Picture of the day for{' '}
				{Date(date).toLocaleDateString('en-us', {
					weekday: 'long',
					year: 'numeric',
					month: 'short',
					day: 'numeric',
				})}
			</h2>
			{media_type === 'image' ? <img src={url} /> : null}
		</div>
	);
};

export default APoDResult;
